import { useState } from "react";
import styled from "styled-components";

const Login = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()

        console.log(email, password)
    }

    return(
        <loginStyled>
        <form className="login" onSubmit={handleSubmit}>
            <h3>Log in</h3>

            <label>Email:</label>
            <input 
            type="email"
              onChange={(e) => setEmail(e.target.value)}
                value={email}
            />
             <label>Password:</label>
            <input 
            type="password"
              onChange={(e) => setPassword(e.target.value)}
                value={password}
            />
            <button>Log in</button>

        </form>
        </loginStyled>
    )
}

const loginStyled = styled.div`
.login {
    max-width: 400px;
    margin: auto;
    padding: 2rem;
    border: 1px solid #ddd;
    border-radius: 10px;
    background: #fff;
}

.login h3 {
    text-align: center;
}

.login label {
    display: block;
    margin-bottom: 0.5rem;
}

.login input {
    width: 100%;
    padding: 0.75rem;
    margin-bottom: 1rem;
    border-radius: 5px;
    border: 1px solid #ddd;
}

.login button {
    width: 100%;
    padding: 0.75rem;
    background: #6c63ff;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
}

.login .error {
    color: red;
    margin-bottom: 1rem;
    text-align: center;
}

`;
export default Login
