import React from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '.../';

const Home = () => {
  const { totalIncome, totalExpenses, totalBalance, transactionHistory } = useGlobalContext();

  return (
    <HomeStyled>
      <div className="welcome-section">
        <h1>Welcome to Your Expenses Tracker</h1>
        <p>Manage your finances with ease.</p>
      </div>

      <div className="summary-section">
        <div className="summary-card">
          <h3>Total Income</h3>
          <p>${totalIncome().toFixed(2)}</p>
        </div>
        <div className="summary-card">
          <h3>Total Expenses</h3>
          <p>${totalExpenses().toFixed(2)}</p>
        </div>
        <div className="summary-card balance">
          <h3>Balance</h3>
          <p>${totalBalance().toFixed(2)}</p>
        </div>
      </div>

      <div className="recent-transactions">
        <h3>Recent Transactions</h3>
        <ul>
          {transactionHistory().map((transaction, index) => (
            <li key={index}>
              <p>{transaction.description}</p>
              <span>{transaction.amount > 0 ? `+ $${transaction.amount}` : `- $${Math.abs(transaction.amount)}`}</span>
            </li>
          ))}
        </ul>
      </div>

      <div className="quick-links">
        <button>View Incomes</button>
        <button>View Expenses</button>
        <button>Add Transaction</button>
      </div>
    </HomeStyled>
  );
};

const HomeStyled = styled.div`
  .welcome-section {
    text-align: center;
    margin-bottom: 2rem;
    h1 {
      font-size: 2.5rem;
      color: #222260;
    }
    p {
      font-size: 1.2rem;
      color: #555;
    }
  }

  .summary-section {
    display: flex;
    justify-content: space-around;
    margin-bottom: 2rem;

    .summary-card {
      background: rgba(252, 246, 249, 0.78);
      border-radius: 15px;
      padding: 1.5rem;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
      text-align: center;
      h3 {
        font-size: 1.2rem;
        margin-bottom: 0.5rem;
        color: #222260;
      }
      p {
        font-size: 1.5rem;
        font-weight: bold;
        color: #222260;
      }
      &.balance {
        background: #222260;
        color: #fff;
      }
    }
  }

  .recent-transactions {
    margin-bottom: 2rem;
    h3 {
      font-size: 1.5rem;
      margin-bottom: 1rem;
      color: #222260;
    }
    ul {
      list-style-type: none;
      padding: 0;
      li {
        background: #f9f9f9;
        padding: 1rem;
        margin-bottom: 0.5rem;
        display: flex;
        justify-content: space-between;
        border-radius: 10px;
        p {
          margin: 0;
          font-size: 1rem;
        }
        span {
          font-size: 1rem;
          font-weight: bold;
          color: #222260;
        }
      }
    }
  }

  .quick-links {
    display: flex;
    justify-content: space-around;
    button {
      background: #222260;
      color: #fff;
      border: none;
      padding: 0.8rem 1.5rem;
      border-radius: 10px;
      cursor: pointer;
      font-size: 1rem;
      transition: background 0.3s ease;
      &:hover {
        background: #5555b0;
      }
    }
  }
`;

export default Home;
