// src/components/Navigation.jsx
import React, { useState } from 'react';
import styled from 'styled-components';
import avatar from '../../img/avatar.png'; // Ensure this path is correct
import backgroundImage from '../../assets/images/nav-background.jpg'; // Ensure this path is correct
import { signout } from '../../utils/Icons'; // Ensure this is correctly imported
import { menuItems } from '../../utils/menuItems';
import Login from '../../pages/Login';
import Signup from '../../pages/Signup';
import { useAuthContext } from '../../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

function Navigation({ active, setActive }) {
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);
    const navigate = useNavigate();

    const { user, logout } = useAuthContext();

    const handleToggleLogin = () => {
        setShowLogin(!showLogin);
        setShowSignup(false);
    };

    const handleToggleSignup = () => {
        setShowSignup(!showSignup);
        setShowLogin(false);
    };

    const handleLogout = () => {
        console.log('Logout button clicked'); // Debugging
        logout();
        console.log('User after logout:', user); // Debugging
        navigate('/'); // Redirect to home after logout
    };

    // Handle successful login/signup
    const handleAuthSuccess = () => {
        setShowLogin(false);
        setShowSignup(false);
    };

    return (
        <NavStyled>
            <nav>
                {user ? (
                    <div className="bottom-nav">
                        <Link to="/home" className="home-link">
                            {/* Add Home Icon or Text */}
                            Home
                        </Link>
                        <span>{user.email}</span>
                        <button className='logout-button' onClick={handleLogout}>
                            {signout} Log Out
                        </button>
                    </div>
                ) : (
                    <div className="auth-buttons">
                        <button className='login-button' onClick={handleToggleLogin}>
                            Login
                        </button>
                        <button className='signup-button' onClick={handleToggleSignup}>
                            Sign Up
                        </button>
                    </div>
                )}
            </nav>

            {/* Display Login or Signup Form */}
            {(showLogin || showSignup) && (
                <FormContainer>
                    {showLogin && <Login onSuccess={handleAuthSuccess} />}
                    {showSignup && <Signup onSuccess={handleAuthSuccess} />}
                </FormContainer>
            )}

            {/* Display Avatar and Menu Items */}
            {user && !showLogin && !showSignup && (
                <>
                    <div className="user-con">
                        <img src={avatar} alt="User Avatar" />
                        <div className="text">
                            <h2>{user.username || user.email}</h2>
                            <p>Your Money</p>
                        </div>
                    </div>

                    <ul className="menu-items">
                        {menuItems.map((item) => (
                            <li
                                key={item.id}
                                onClick={() => setActive(item.id)}
                                className={active === item.id ? 'active' : ''}
                            >
                                {item.icon}
                                <span>{item.title}</span>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </NavStyled>
    );
}

const NavStyled = styled.nav`
    /* Apply the background image */
    background-image: url(${backgroundImage});
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;

    /* Overlay for better readability */
    position: relative;
    &::before {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
