        background: rgba(252, 246, 249, 0.78); /* Adjust opacity and color as needed */
        z-index: 0;
        border: 3px solid #FFFFFF;
        backdrop-filter: blur(4.5px);
        border-radius: 32px;
    }

    /* Main container styles */
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100vh; /* Adjusted to full viewport height */
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;
    position: relative; /* To position overlay correctly */
    z-index: 1;

    /* Navigation Buttons */
    .bottom-nav {
        display: flex;
        align-items: center;
        justify-content: space-between;
        z-index: 2; /* Above overlay */
    }

    .auth-buttons {
        display: flex;
        gap: 10px;
        z-index: 2;
    }

    /* User Avatar and Info */
    .user-con {
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;
        z-index: 2;

        img {
            width: 80px;
            height: 80px;
            border-radius: 50%;
            object-fit: cover;
            background: #fcf6f9;
            border: 2px solid #FFFFFF;
            padding: 0.2rem;
            box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
        }

        .text {
            h2 {
                color: rgba(34, 34, 96, 1);
                text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.2);
            }

            p {
                color: rgba(34, 34, 96, 0.6);
                text-shadow: 1px 1px 1px rgba(255, 255, 255, 0.2);
            }
        }
    }

    /* Menu Items */
    .menu-items {
        flex: 1;
        display: flex;
        flex-direction: column;
        z-index: 2;

        li {
            display: grid;
            grid-template-columns: 40px auto;
