import React, { useState } from 'react';
import styled from 'styled-components';
import avatar from '../../img/avatar.png';
import backgroundImage from '../'; // Adjust the path as needed
import { signout } from '../../utils/Icons';
import { menuItems } from '../../utils/menuItems';
import Login from '../../pages/Login';  // Import the Login component
import Signup from '../../pages/Signup'; // Import the Signup component
import { useLogout } from '../../hooks/useLogout';
import { useAuthContext } from '../../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';  // Consolidated imports

function Navigation({ active, setActive }) {
    const [showLogin, setShowLogin] = useState(false);  // Initially set to false
    const [showSignup, setShowSignup] = useState(false);
    const navigate = useNavigate();  // Initialize useNavigate hook
   
    const handleToggleLogin = () => {
        setShowLogin(!showLogin);  // Toggle login form visibility
        setShowSignup(false);  // Ensure signup form is hidden when login is shown
    };

    const handleToggleSignup = () => {
        setShowSignup(!showSignup);  // Toggle signup form visibility
        setShowLogin(false);  // Ensure login form is hidden when signup is shown
    };

    // Logout 
    const { logout } = useLogout();
    const { user } = useAuthContext();
    
    const handleLogout = () => {
        logout();
        navigate('/');  // Redirect to home after logout
    };

    return (
        <NavStyled>
            <nav>
                {user ? (
                    <div className="bottom-nav">
                        <Link to="/home" className="home-link">
                            {/* You can add a home icon or text here */}
                            Home
                        </Link>
                        <span>{user.email}</span>
                        <button className='logout-button' onClick={handleLogout}>
                            {signout} Log Out
                        </button>
                    </div>
                ) : (
                    <div className="auth-buttons">
                        <button className='login-button' onClick={handleToggleLogin}>
                            Login
                        </button>
                        <button className='signup-button' onClick={handleToggleSignup}>
                            Sign Up
                        </button>
                    </div>
                )}
            </nav>

            {/* Centered Forms */}
            { (showLogin || showSignup) && (
                <FormContainer>
                    {showLogin && <Login onSuccess={() => { setShowLogin(false); }} />}
                    {showSignup && <Signup onSuccess={() => { setShowSignup(false); }} />}
                </FormContainer>
            )}

            {/* User Avatar and Menu Items - Only show when user is logged in and forms are not visible */}
            {user && !showLogin && !showSignup && (
                <>
                    <div className="user-con">
                        <img src={avatar} alt="User Avatar" />
                        <div className="text">
                            <h2>{user.username || user.email}</h2>
                            <p>Your Money</p>
                        </div>
                    </div>

                    <ul className="menu-items">
                        {menuItems.map((item) => (
                            <li
                                key={item.id}
                                onClick={() => setActive(item.id)}
                                className={active === item.id ? 'active' : ''}
                            >
                                {item.icon}
                                <span>{item.title}</span>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </NavStyled>
    );
}

const NavStyled = styled.nav`
    /* Apply the background image */
    background-image: url(${backgroundImage});
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    
    /* Optional: Add a semi-transparent overlay for better readability */
    position: relative;
    &::before {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: rgba(252, 246, 249, 0.78); /* Adjust opacity and color as needed */
        z-index: 0; /* Ensure it stays behind the content */
        border: 3px solid #FFFFFF;
        backdrop-filter: blur(4.5px);
        border-radius: 32px;
    }

    /* Adjust existing styles to account for the overlay */
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;
    position: relative; /* To position the overlay correctly */
    z-index: 1; /* Ensure content stays above the overlay */

    .bottom-nav {
        display: flex;
        align-items: center;
        justify-content: space-between;
        z-index: 2; /* Above overlay */
    }

    .auth-buttons {
        display: flex;
        gap: 10px;
        z-index: 2;
    }
       
    .user-con {
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;
        z-index: 2;

        img {
            width: 80px;
            height: 80px;
            border-radius: 50%;
            object-fit: cover;
            background: #fcf6f9;
            border: 2px solid #FFFFFF;
            padding: .2rem;
            box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
        }

        h2 {
            color: rgba(34, 34, 96, 1);
            text-shadow: 1px 1px 2px rgba(0,0,0,0.2);
        }

        p {
            color: rgba(34, 34, 96, .6);
            text-shadow: 1px 1px 1px rgba(255,255,255,0.2);
        }
    }

    .menu-items {
        flex: 1;
        display: flex;
        flex-direction: column;
        z-index: 2;

        li {
            display: grid;
            grid-template-columns: 40px auto;
            align-items: center;
            margin: .6rem 0;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: rgba(34, 34, 96, .6);
            padding-left: 1rem;
            position: relative;

            i {
                color: rgba(34, 34, 96, 0.6);
                font-size: 1.4rem;
                transition: all .4s ease-in-out;
            }
        }
    }

    .active {
        color: rgba(34, 34, 96, 1) !important;

        i {
            color: rgba(34, 34, 96, 1) !important;
        }

        &::before {
            content: "";
            position: absolute;
            left: 0;
            top: 0;
            width: 4px;
            height: 100%;
            background: #222260;
            border-radius: 0 10px 10px 0;
        }
    }

    .login-button, .signup-button {
        padding: 10px 20px;
        color: #fff;
        background-color: var(--primary); /* Ensure --primary is defined in your CSS */
        border: none;
        border-radius: 5px;
        cursor: pointer;
        transition: background-color 0.3s ease;
        font-size: 1em;
        z-index: 2;
    }

    .login-button:hover, .signup-button:hover {
        background-color: #0056b3; /* Replace with a darker shade or use a function if using SCSS */
    }

    .logout-button {
        background: #fff;
        color: var(--primary);
        border: 2px solid var(--primary);
        padding: 6px 10px;
        border-radius: 4px;
        font-family: "Poppins", sans-serif;
        cursor: pointer;
        font-size: 1em;
        transition: background-color 0.3s ease, color 0.3s ease;
        z-index: 2;
    }

    .logout-button:hover {
        background-color: var(--primary);
        color: #fff;
    }

    /* Responsive adjustments */
    @media (max-width: 768px) {
        width: 100%; /* Adjust width for smaller screens */
        background-position: top; /* Change position if needed */
    }
`;

const FormContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    width: 100%;
    
    background: rgba(255, 255, 255, 0.9);
    border-radius: 20px;
    padding: 2rem;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1);
    z-index: 2; /* Ensure it stays above the overlay */
`;

export default Navigation;
